import React, { useState, useEffect } from "react";
import Clock from "./Clock";
import { useSelector } from 'react-redux';

const ColumnNew = () => {
    const {account, domain} = useSelector((store) => store);
    const [nfts, setNfts] = useState([]);

    useEffect(() => {
        fetch(domain + "/api/user/nfts/" + account).then((res) => res.json()).then((res) => {
            setNfts(res["result"]);
            console.log(nfts);
        });
    }, []);

    const [state, setState] = useState({
        nfts: nfts.slice(0,8),
        height: 0
    });


    const loadMore = () => {
        let nftState = state.nfts;
        let start = nftState.length;
        let end = nftState.length+4;
        setState({...state, nfts: [...nftState, ...(nfts.slice(start, end))]})
    }


    const onImgLoad = (event) => {
        let img = event.target;
        let currentHeight = state.height;
        if(currentHeight < img.offsetHeight) {
            setState({...state, height: img.offsetHeight})
        }
    }


    return (
        <div className='row'> 
            {state.nfts.map((nft, index) => ( 
                <div key={index} className="d-item col-lg-3 col-md-6 col-sm-6 col-xs-12 mb-4">
                    <div className="nft__item m-0">
                        { nft.deadline &&
                            <div className="de_countdown">
                                <Clock deadline={nft.deadline} />
                            </div>
                        }
                        <div className="author_list_pp">
                            <span onClick={()=> window.open(nft.authorLink, "_self")}>                                    
                                <img className="lazy" src={nft.authorImg} alt=""/>
                                <i className="fa fa-check"></i>
                            </span>
                        </div>
                        <div className="nft__item_wrap" style={{height: `${state.height}px`}}>
                            <span>
                                <img onLoad={onImgLoad} src={nft.previewImg} className="lazy nft__item_preview" alt=""/>
                            </span>
                        </div>
                        <div className="nft__item_info">
                            <span onClick={()=> window.open(nft.nftLink, "_self")}>
                                <h4>{nft.title}</h4>
                            </span>
                            <div className="nft__item_price">
                                {nft.price}<span>{nft.bid}</span>
                            </div>
                            <div className="nft__item_action">
                                <span onClick={()=> window.open(nft.bidLink, "_self")}>Place a bid</span>
                            </div>
                            <div className="nft__item_like">
                                <i className="fa fa-heart"></i><span>{nft.likes}</span>
