import React, { useState, useEffect } from "react";
import { useSelector } from 'react-redux';
import { Redirect } from '@reach/router';

const ColumnNew = () => {
    const {account, domain} = useSelector((store) => store);
    const [nftList, setNftList] = useState([]);
    const [state, setState] = useState({
        nfts: [],
        height: 0
    });



    const loadMore = () => {
        let nftState = state.nfts;
        let start = nftState.length;
        let end = nftState.length+4;
        setState({...state, nfts: [...nftState, ...(nftList.slice(start, end))]})
    }


    const onImgLoad = (event) => {
        let img = event.target;
        let currentHeight = state.height;
        if(currentHeight < img.offsetHeight) {
            setState({...state, height: img.offsetHeight})
        }
    }

    console.log("aaa");

    return (
        <>
        {account
        ?<div className='row'> 
            {state.nfts.map((nft, index) => ( 
                <div key={index} className="d-item col-lg-3 col-md-6 col-sm-6 col-xs-12 mb-4">
                <div className="nft__item m-0">
                    <div className="nft__item_wrap" style={{height: `${state.height}px`}}>
                        <span>
                            <img onLoad={onImgLoad} src={nft.originUrl} className="lazy nft__item_preview" alt=""/>
                        </span>
                    </div>
                    <div className="nft__item_info">
                        <span>
                            <h4>{nft.name}</h4>
                        </span>
                    </div> 
                </div>
            </div> 
            ))}
            { state.nfts.length !== nftList.length &&
                <div className='col-lg-12'>
                    <div className="spacer-single"></div>
                    <span onClick={() => loadMore()} className="btn-main lead m-auto">Load More</span>
                </div>
            }
        </div>
        :<Redirect noThrow={true} to="/explore"/>
        }
        </>
    );
};

export default ColumnNew;
